    driver.FindElement(By.CssSelector(".show #item-2 > .text")).Click();
    driver.FindElement(By.CssSelector(".custom-control:nth-child(2) > .custom-control-label")).Click();
    Assert.True(driver.FindElement(By.Id("yesRadio")).Selected);
