    driver.FindElement(By.CssSelector(".custom-control:nth-child(3) > .custom-control-label")).Click();
    Assert.True(driver.FindElement(By.Id("impressiveRadio")).Selected);
    Assert.False(driver.FindElement(By.Id("yesRadio")).Selected);
    Assert.Equal(driver.FindElement(By.CssSelector(".mt-3")).Text, "You have selected Impressive");
